import { useEffect, useState } from "react";

const usePackages = () => {
  const [packages, setPackages] = useState([]);
  useEffect(() => {
    fetch("http://localhost:5000/package")
      .then((res) => res.json())
      .then((data) => setPackages(data));
  }, []);

  return { packages };
};

export default usePackages;
